mapper.select:

where 1 = 1 

	and id = #{id} 


	and req_id = #{req_id} 


	and req_token = #{req_token} 


	and req_ip = #{req_ip} 


	and req_api = #{req_api} 


	and res_code = #{res_code} 


	and user_id = #{user_id} 


	and admin_id = #{admin_id} 


	and start_time >= #{s_time} 


	and start_time <= #{e_time} 

order by 

	id desc 
	start_time desc, id desc 
	cost_time desc, id desc 
 	id desc
